SkipRolloutTimer:
	mov r0, #0x4	@ max timer

ReturnRolloutTimer:
	lsl r0, r0, #0x18
	lsr r5, r0, #0x18
	ldr r0, =(0x080b4c48 +1)
	bx r0

GetRolloutTimer:
	ldr r0, .GetRolloutTimer
	bx r0

