	mov r3, #0x80
	lsl r3, #0x9
	add r0, r2, r3
	lsr r4, r0, #0x10
	push {r0}
	bl ShadowSneakAnimHelper
	cmp r0, #0x0
	pop {r0}
	bne EndDestinyBondLoop
	ldr r1, .DestinyBondContinueLoop
	bx r1

EndDestinyBondLoop:
	ldr r0, .DestinyBondEndLoop
	bx r0

