.align 2
DISABLE_TIME_ASM:
	ldr r1, =MOVE_LETS_SNUGGLE_FOREVER
	ldr r4, =CURRENT_MOVE
	ldrh r4, [r4]
	cmp r1, r4
	beq SnuggleForeverTime
	mov r1, #0x50
	b StoreDisableTimer
